import { throwError } from 'rxjs';
import { HttpClient } from '@angular/common/http';
import { map, catchError } from 'rxjs/operators';
export class BaseResourceService {
  constructor(apiPath, injector) {
    this.apiPath = apiPath;
    this.injector = injector;
    this.http = injector.get(HttpClient);
  }

  getAll() {
    return this.http.get(this.apiPath).pipe(catchError(this.handleError), map(this.jsonDataToCategories));
  }

  getById(id) {
    const url = `${this.apiPath}/${id}`;
    return this.http.get(url).pipe(catchError(this.handleError), map(this.jsonDataToResource));
  }

  created(resource) {
    return this.http.post(this.apiPath, resource).pipe(catchError(this.handleError), map(this.jsonDataToResource));
  }

  update(resource) {
    const url = `${this.apiPath}/${resource.id}`;
    return this.http.put(url, resource).pipe(catchError(this.handleError), map(this.jsonDataToResource));
  }

  delete(id) {
    const url = `${this.apiPath}/${id}`;
    return this.http.delete(url).pipe(catchError(this.handleError), map(() => null));
  }

  jsonDataToCategories(jsonData) {
    const categories = [];
    jsonData.forEach(element => categories.push(element));
    return categories;
  }

  jsonDataToResource(jsonData) {
    return jsonData;
  }

  handleError(error) {
    console.log("Erro na requisicao =>", error);
    return throwError(error);
  }

}
